// Merge Appeal Data + Fields Config
// Получаем данные из обоих SQL запросов

let appealData = null;
let fieldsConfig = [];
let inputData = null;

// Получаем входные данные (appeal.id, tenant_id, text, channel и т.д.)
try {
  inputData = $('Execute Workflow Trigger').first()?.json || {};
} catch (e) {
  inputData = {};
}

// Получаем данные заявки из Load Appeal & Devices
try {
  appealData = $('Load Appeal & Devices').first()?.json || null;
} catch (e) {
  console.error('Could not get appeal data:', e.message);
}

// Получаем конфиг полей из Load Fields Config
try {
  const fieldsItems = $('Load Fields Config').all();
  fieldsConfig = fieldsItems.map(item => item.json);
} catch (e) {
  console.error('Could not get fields config:', e.message);
}

console.log('=== MERGE DATA ===');
console.log('Appeal ID:', appealData?.id);
console.log('Fields config count:', fieldsConfig.length);
console.log('Input text:', inputData?.text?.substring(0, 50));

if (!appealData || !appealData.id) {
  throw new Error('Appeal data not loaded!');
}

// Возвращаем объединённые данные
return {
  // Данные заявки
  ...appealData,

  // Входные данные (text, channel, external_chat_id)
  text: inputData.text,
  channel: inputData.channel || appealData.channel,
  external_chat_id: inputData.external_chat_id,
  external_user_id: inputData.external_user_id,

  // Конфиг полей для extraction
  fields_config: fieldsConfig,

  // Сохраняем appeal как вложенный объект тоже
  appeal: {
    id: appealData.id,
    tenant_id: appealData.tenant_id,
    client_id: appealData.client_id,
    stage: appealData.stage,
    channel: appealData.channel || inputData.channel,
    operation_mode: appealData.operation_mode
  }
};

// Prepare context with device disambiguation, conversation focus, and completeness check
const data = $input.first().json;
const devices = data.devices || [];
const appeal = data;
const history = data.messages || [];
const fieldsConfig = data.fields_config || [];
const text = data.text || '';

console.log('=== PREPARE CONTEXT ===');
console.log('Appeal ID:', appeal.id);
console.log('Devices count:', devices.length);
console.log('Fields config count:', fieldsConfig.length);
console.log('Message text:', text.substring(0, 50));

// Текущий фокус из appeal
const currentFocus = appeal.conversation_focus || {};
const lastDeviceId = appeal.last_mentioned_device_id;
const lastRepairId = appeal.last_mentioned_repair_id;

// Group devices by brand+model
const groups = {};
devices.forEach(d => {
  const key = `${d.brand_name || ''} ${d.model_name || ''}`.trim();
  if (!groups[key]) groups[key] = [];
  groups[key].push(d);
});

// Check if disambiguation needed
let needsDisambiguation = false;
let disambiguationInfo = '';
let ambiguousDevices = [];

for (const [model, devs] of Object.entries(groups)) {
  if (devs.length > 1) {
    const withoutRepairs = devs.filter(d => !d.repairs || d.repairs.length === 0);
    if (withoutRepairs.length > 1) {
      needsDisambiguation = true;
      disambiguationInfo += `Несколько ${model} (${devs.length} шт). `;
      ambiguousDevices.push(...devs.map(d => d.id));
    }
  }
}

// Определяем текущий фокус
let focusDeviceId = lastDeviceId;
let focusRepairId = lastRepairId;
let focusDeviceOrder = currentFocus.device_order || 1;

if (devices.length === 1) {
  focusDeviceId = devices[0].id;
  focusDeviceOrder = devices[0].device_order || 1;
  if (devices[0].repairs && devices[0].repairs.length === 1) {
    focusRepairId = devices[0].repairs[0].id;
  }
}

if (!focusDeviceId && devices.length > 0) {
  focusDeviceId = devices[0].id;
  focusDeviceOrder = devices[0].device_order || 1;
}

// Build device list for context
const deviceList = devices.map((d, i) => {
  const isFocused = d.id === focusDeviceId ? ' ← В ФОКУСЕ' : '';
  const repairs = d.repairs?.map(r => r.category_name || r.repair_type).join(', ') || 'нет ремонтов';
  const ownerLabel = d.owner_label ? ` (${d.owner_label})` : '';
  return `#${d.device_order || i+1}: ${d.brand_name || '?'} ${d.model_name || '?'}${ownerLabel} - ${repairs}${isFocused}`;
}).join('\n');

// ============ COMPLETENESS CHECK ============
// Проверяем заполненность обязательных полей
const firstDevice = devices[0] || {};
const firstRepair = firstDevice.repairs?.[0] || {};

// Текущее состояние данных
const currentState = {
  brand: appeal.brand_name || firstDevice.brand_name || null,
  brand_id: appeal.brand_id || firstDevice.brand_id || null,
  model: appeal.model_name || firstDevice.model_name || null,
  model_id: appeal.model_id || firstDevice.model_id || null,
  repair_category: firstRepair.category_name || null,
  repair_category_id: firstRepair.category_id || null
};

// Маппинг field_name -> ключ в currentState
const fieldMapping = {
  'brand': 'brand_id',
  'model': 'model_id',
  'repair_type': 'repair_category_id',
  'repair_category': 'repair_category_id',
  'type': 'repair_category_id'  // alias
};

// Проверяем какие обязательные поля заполнены
const requiredFields = fieldsConfig.filter(f => f.is_required);
const missingFields = [];
let filledCount = 0;

for (const field of requiredFields) {
  const stateKey = fieldMapping[field.field_name] || field.field_name;
  if (currentState[stateKey]) {
    filledCount++;
  } else {
    missingFields.push(field.field_name);
  }
}

const totalRequired = requiredFields.length || 1;
const completionPercentage = Math.round((filledCount / totalRequired) * 100);
const isComplete = missingFields.length === 0 && devices.length > 0 && firstDevice.brand_id && firstRepair.category_id;

console.log('Required fields:', requiredFields.map(f => f.field_name));
console.log('Missing fields:', missingFields);
console.log('Completion:', completionPercentage + '%');
console.log('Is complete:', isComplete);

// Новый conversation_focus
const newFocus = {
  device_id: focusDeviceId,
  repair_id: focusRepairId,
  device_order: focusDeviceOrder,
  updated_at: new Date().toISOString()
};

return {
  json: {
    // Основные данные
    ...data,
    appeal: data.appeal,
    devices,
    deviceList,
    history,
    text,

    // Disambiguation
    needsDisambiguation,
    disambiguationInfo,
    ambiguousDevices,
    deviceCount: devices.length,

    // Фокус диалога
    conversation_focus: newFocus,
    focus_device_id: focusDeviceId,
    focus_repair_id: focusRepairId,
    focus_device_order: focusDeviceOrder,

    // Completeness (для Needs Extraction? и дальше)
    is_complete: isComplete,
    completion_percentage: completionPercentage,
    missing_fields: missingFields,
    current_state: currentState,
    extracted_data: currentState,  // alias для совместимости
    fields_config: fieldsConfig
  }
};

// Skip extraction - all data already collected
const context = $input.first().json;

console.log('=== SKIP EXTRACTION ===');
console.log('Appeal ID:', context.appeal?.id);
console.log('Is complete:', context.is_complete);

// Просто пробрасываем данные дальше
return {
  action: 'skip_extraction',
  ...context,
  total_tasks: 0,
  completed: 0,
  elapsed_ms: 0
};

// Merge Results - объединяем результаты из Task Dispatcher или Skip Extraction
const items = $input.all();

console.log('=== MERGE RESULTS ===');
console.log('Received items:', items.length);

if (items.length === 0) {
  // Fallback на Prepare Context
  try {
    const ctx = $('Prepare Context').first()?.json;
    if (ctx) {
      console.log('Using fallback from Prepare Context');
      return ctx;
    }
  } catch (e) {}
  return [];
}

// Берём первый item
const result = items[0].json;

// Если пришло из Task Dispatcher - мержим с контекстом
let context = null;
try {
  context = $('Prepare Context').first()?.json;
} catch (e) {}

if (context && result.action !== 'skip_extraction') {
  // Мержим результаты extraction с контекстом
  const merged = {
    ...context,
    ...result,
    dispatcher_result: result
  };

  // Обновляем extracted_data если есть новые данные
  if (result.extracted_data) {
    merged.extracted_data = {
      ...context.extracted_data,
      ...result.extracted_data
    };
  }

  console.log('Merged with dispatcher result');
  return merged;
}

console.log('Passing through:', Object.keys(result));
return result;

// Build AI context string for the LLM
const data = $input.first().json;
const { appeal, devices, deviceList, history, needsDisambiguation, disambiguationInfo, text, missing_fields } = data;

console.log('=== BUILD AI CONTEXT ===');
console.log('Appeal ID:', appeal?.id);
console.log('Text:', text?.substring(0, 50));

let context = '';

// If need to clarify which device
if (needsDisambiguation) {
  context += `⚠️ УТОЧНИ УСТРОЙСТВО: ${disambiguationInfo}Спроси клиента какое именно устройство обсуждаем.\n\n`;
}

// Appeal info
context += `Заявка #${appeal?.id || data.id}\n`;
context += `Канал: ${data.channel || appeal?.channel}\n`;
if (appeal?.conversation_context) context += `Контекст: ${appeal.conversation_context}\n`;

// Devices
if (deviceList) {
  context += `\nУстройства клиента:\n${deviceList}\n`;
} else {
  context += `\nУстройств пока нет\n`;
}

// Missing fields
if (missing_fields && missing_fields.length > 0) {
  context += `\nНужно уточнить: ${missing_fields.join(', ')}\n`;
}

// Recent history
if (history && history.length > 0) {
  const recent = history.slice(-5);
  context += `\nИстория (последние ${recent.length}):\n`;
  recent.forEach(h => {
    const sender = h.is_client ? 'Клиент' : 'Мы';
    context += `[${sender}] ${h.text?.substring(0, 100) || ''}\n`;
  });
}

// Current message
if (text) {
  context += `\nТекущее сообщение клиента: "${text}"\n`;
}

console.log('Context built, length:', context.length);

return { 
  json: { 
    ...data,
    context,
    chatInput: text || 'Здравствуйте'
  } 
};

// Prepare Response - финальная подготовка ответа
const context = $('Build AI Context').first().json;
const aiResponse = $input.first().json;

const responseText = aiResponse.output || aiResponse.text || 'Здравствуйте! Чем могу помочь?';

console.log('=== PREPARE RESPONSE ===');
console.log('AI Response:', responseText.substring(0, 50));

// Безопасное получение данных
const extracted_data = context.extracted_data || {};
const missing_fields = context.missing_fields || [];

// Определяем следующее действие
let nextAction = 'continue';
if (context.is_complete) {
  nextAction = 'calculate_cost';
} else if (missing_fields.length > 0) {
  nextAction = 'wait_clarification';
}

// Определяем маршрут для обновления БД
let route = 'minimal';
if (extracted_data.brand_id && extracted_data.model_id && extracted_data.repair_category_id) {
  route = 'full';
} else if (extracted_data.brand_id || extracted_data.model_id) {
  route = 'partial';
}

// Режим работы AI
const operationMode = context.appeal?.operation_mode || context.operation_mode || 'assist';

// IDs
const appeal_id = context.appeal?.id || context.id;
const tenant_id = context.tenant_id || context.appeal?.tenant_id;

console.log('Appeal ID:', appeal_id);
console.log('Route:', route);
console.log('Operation mode:', operationMode);

return {
  appeal_id: appeal_id,
  tenant_id: tenant_id,
  channel: context.channel || context.appeal?.channel,
  external_chat_id: context.external_chat_id,
  client_name: context.client_name,
  response_text: responseText,
  next_action: nextAction,
  route: route,
  extracted_data: extracted_data,
  completion_percentage: context.completion_percentage || 0,
  dispatcher_result: context.dispatcher_result,
  devices: context.devices,
  device_count: context.deviceCount || 0,
  conversation_focus: context.conversation_focus,
  focus_device_id: context.focus_device_id,
  focus_repair_id: context.focus_repair_id,
  operation_mode: operationMode,

  // Для Operator Notifier
  approval_data: {
    channel: context.channel,
    external_chat_id: context.external_chat_id,
    appeal_id: appeal_id,
    client_name: context.client_name,
    response_text: responseText,
    next_action: nextAction,
    appeal: context.appeal,
    extracted_data: extracted_data,
    operation_mode: operationMode,
    completeness: {
      percentage: context.completion_percentage || 0,
      missing: missing_fields
    }
  }
};

// Merge After Update - берём данные из Prepare Response
let data = null;

try {
  data = $('Prepare Response').first().json;
  console.log('Got data from Prepare Response');
} catch (e) {
  console.log('Could not access Prepare Response:', e.message);
  const items = $input.all();
  if (items.length > 0) {
    data = items[0].json;
  }
}

console.log('=== MERGE AFTER UPDATE ===');
console.log('Appeal ID:', data?.appeal_id);
console.log('Response text:', data?.response_text?.substring(0, 50));

return data || {};

// Prepare Notifier Data для режима assist
let data = null;

try {
  data = $('Prepare Response').first().json;
} catch (e) {
  data = $input.first()?.json || {};
}

console.log('=== PREPARE NOTIFIER DATA ===');

return {
  tenant_id: data.tenant_id,
  appeal_id: data.appeal_id,
  client_name: data.client_name || '',
  channel: data.channel,
  text: data.text || '',
  response_text: data.response_text || '',
  external_chat_id: data.external_chat_id,
  appeal: data.appeal,
  missing_fields: data.missing_fields
};

// Prepare Auto Send для режима auto
const data = $input.first().json;

console.log('=== PREPARE AUTO SEND ===');

let clientId = null;
try {
  const ctx = $('Build AI Context').first()?.json;
  clientId = ctx?.appeal?.client_id || ctx?.client_id;
} catch (e) {}

if (!clientId) {
  clientId = data.approval_data?.appeal?.client_id;
}

if (!clientId) {
  throw new Error('client_id not found');
}

return {
  appeal_id: data.appeal_id,
  client_id: clientId,
  response_to_send: data.response_text,
  channel: data.channel || 'telegram',
  response_type: 'text',
  operator_action: 'auto_response',
  operator_name: 'ИИ-агент',
  client_telegram_id: data.external_chat_id,
  source_workflow: 'BAT AI Appeal Router (auto)',
  extracted_data: data.extracted_data,
  completion_percentage: data.completion_percentage,
  approval_data: data.approval_data,
  operation_mode: data.operation_mode,
  client_name: data.client_name
};

