const input = $('Parse Input').first().json;
const operators = $input.all();

console.log('‚ïî‚ïê‚ïê‚ïê MERGE WITH OPERATORS ‚ïê‚ïê‚ïê');
console.log('Operators count:', operators.length);

if (operators.length === 0) {
  console.log('‚ö†Ô∏è No operators with FCM tokens found');
  return [{
    json: {
      ...input,
      operators_count: 0,
      skip_reason: 'no_operators_with_fcm'
    }
  }];
}

const result = operators.map(op => {
  const operatorData = op.json;

  return {
    ...input,
    operator_id: operatorData.id,
    operator_name: operatorData.name,
    fcm_token: operatorData.fcm_token
  };
});

console.log('‚úÖ Merged with', result.length, 'operators');

return result;

const items = $input.all();

const total = items.length;
const sent = items.filter(i => i.json.fcm_sent === true).length;
const failed = items.filter(i => i.json.fcm_sent === false && !i.json.skipped).length;
const skipped = items.filter(i => i.json.skipped || i.json.fcm_skip_reason).length;

console.log('‚ïî‚ïê‚ïê‚ïê FCM SENDER SUMMARY ‚ïê‚ïê‚ïê');
console.log('Total operators:', total);
console.log('‚úÖ Sent:', sent);
console.log('‚ùå Failed:', failed);
console.log('‚ö†Ô∏è Skipped:', skipped);

return {
  success: true,
  notification_type: items[0]?.json?.notification_type || 'unknown',
  total_operators: total,
  sent_count: sent,
  failed_count: failed,
  skipped_count: skipped,
  timestamp: new Date().toISOString()
};

