// Валидация входных данных для Touchpoint Register
// 3 типа касаний: inbound (новый клиент), mutual (диалог), outbound (промо)

const ALLOWED_CHANNELS = ['whatsapp', 'telegram', 'vk', 'avito', 'max', 'phone', 'email', 'web', 'form', 'in_person'];
const ALLOWED_DIRECTIONS = ['inbound', 'outbound', 'mutual'];
const ALLOWED_TYPES = ['message', 'call', 'visit', 'promo', 'form'];

const body = $json.body || {};

const client_id = body.client_id;
const appeal_id = body.appeal_id || null;
const channel = body.channel || 'unknown';
const direction = body.direction || 'mutual';
const type = body.type || 'message';
const is_new_client = body.is_new_client || false;
const vertical_id = body.vertical_id || null;
const tenant_id = body.tenant_id || null;

// Валидация обязательных полей
if (!client_id) {
  throw new Error('client_id is required');
}

if (!ALLOWED_DIRECTIONS.includes(direction)) {
  throw new Error(`Invalid direction: ${direction}. Allowed: ${ALLOWED_DIRECTIONS.join(', ')}`);
}

// Логика определения direction:
// - is_new_client && нет телефона в базе → inbound
// - is_new_client && есть телефон в базе → mutual  
// - промо/рассылка → outbound
// - диалог (есть inbound + был ответ + клиент написал) → mutual

const touchpoint_id = crypto.randomUUID();

return {
  touchpoint_id,
  client_id,
  appeal_id,
  channel,
  direction,
  type,
  is_new_client,
  vertical_id,
  tenant_id,
  timestamp: new Date().toISOString(),
  _validation: 'passed'
};

