={{ 
  const data = $json;
  const statements = [];

  // 1. Создаём Touchpoint узел
  statements.push({
    statement: `
      CREATE (t:Touchpoint {
        id: $id,
        timestamp: datetime($timestamp),
        type: $type,
        channel: $channel,
        direction: $direction
      })
      RETURN t
    `,
    parameters: {
      id: data.touchpoint_id,
      timestamp: data.timestamp,
      type: data.type,
      channel: data.channel,
      direction: data.direction
    }
  });

  // 2. Связь с Client (FROM для inbound, TO для outbound, оба для mutual)
  if (data.direction === 'inbound') {
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (c:Client {id: $clientId})
        CREATE (t)-[:FROM]->(c)
        RETURN t, c
      `,
      parameters: {
        touchpointId: data.touchpoint_id,
        clientId: data.client_id
      }
    });
  } else if (data.direction === 'outbound') {
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (c:Client {id: $clientId})
        CREATE (t)-[:TO]->(c)
        RETURN t, c
      `,
      parameters: {
        touchpointId: data.touchpoint_id,
        clientId: data.client_id
      }
    });
  } else if (data.direction === 'mutual') {
    // Для mutual - связь в обе стороны
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (c:Client {id: $clientId})
        CREATE (t)-[:FROM]->(c)
        CREATE (t)-[:TO]->(c)
        RETURN t, c
      `,
      parameters: {
        touchpointId: data.touchpoint_id,
        clientId: data.client_id
      }
    });
  }

  // 3. Связь с Vertical если указана
  if (data.vertical_id) {
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (v:Vertical {id: $verticalId})
        CREATE (t)-[:IN_VERTICAL]->(v)
        RETURN t, v
      `,
      parameters: {
        touchpointId: data.touchpoint_id,
        verticalId: data.vertical_id
      }
    });
  }

  JSON.stringify({ statements });
}}

