const neo4jResult = $('Neo4j Create Touchpoint').first()?.json || {};
const pgResult = $json;
const inputData = $('Parse & Validate').first()?.json || {};

// Проверка на ошибки Neo4j
if (neo4jResult.errors && neo4jResult.errors.length > 0) {
  const errorMessages = neo4jResult.errors.map(e => e.message).join('; ');
  return {
    success: false,
    error: 'Neo4j error: ' + errorMessages
  };
}

return {
  success: true,
  touchpoint_id: inputData.touchpoint_id,
  direction: inputData.direction,
  channel: inputData.channel,
  client_id: inputData.client_id,
  neo4j_created: true,
  pg_created: !!pgResult.id
};
