const event = $input.first().json;

// WAPPI API webhook format
const chatId = event.chatId || event.chat_id;
const sender = event.sender || event.from;
const messageText = event.text || event.body || '';
const messageId = event.messageId || event.id || Date.now().toString();
const timestamp = event.timestamp ? new Date(event.timestamp * 1000).toISOString() : new Date().toISOString();

// Извлекаем номер телефона из chatId (формат: 79991234567@c.us)
let phone = chatId.split('@')[0];
if (phone && !phone.startsWith('+')) {
  phone = '+' + phone;
}

// Проверяем медиа
const media = event.media || {};
const hasPhoto = media.type === 'image' || (event.hasMedia && event.media?.mimetype?.startsWith('image/'));
const hasVoice = media.type === 'audio' || media.type === 'voice' || event.media?.mimetype?.startsWith('audio/');
const hasVideo = media.type === 'video' || event.media?.mimetype?.startsWith('video/');
const hasDocument = media.type === 'document' || event.media?.mimetype === 'application/pdf';

const urls = [];
let photoUrl = null;
let voiceUrl = null;
let videoUrl = null;

// WAPPI предоставляет прямые ссылки на медиа
if (media.url) {
  urls.push(media.url);
  if (hasPhoto) photoUrl = media.url;
  if (hasVoice) voiceUrl = media.url;
  if (hasVideo) videoUrl = media.url;
}

// Альтернативный формат для совместимости
if (event.mediaUrl) {
  urls.push(event.mediaUrl);
  if (hasPhoto && !photoUrl) photoUrl = event.mediaUrl;
  if (hasVoice && !voiceUrl) voiceUrl = event.mediaUrl;
  if (hasVideo && !videoUrl) videoUrl = event.mediaUrl;
}

return {
  has_voice: hasVoice,
  voice_url: voiceUrl,
  raw_event: event,
  chat_id: chatId,
  phone: phone,
  message_text: messageText,
  message_id: messageId,
  timestamp: timestamp,
  sender_name: event.senderName || event.pushname || null,
  has_photo: hasPhoto,
  photo_url: photoUrl,
  has_video: hasVideo,
  video_url: videoUrl,
  has_document: hasDocument,
  urls: urls
};

const data = $('Extract WhatsApp Data').first().json;
const transcription = $input.first().json.text;

let messageText = data.message_text;
if (transcription) {
  messageText = (messageText ? messageText + '\n\n' : '') + transcription;
}

return {
  channel: 'whatsapp',
  external_user_id: data.phone?.replace(/\+/g, ''),
  external_chat_id: data.chat_id,

  text: messageText,
  timestamp: data.timestamp,

  client_phone: data.phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: true,
    voice_transcribed_text: transcription,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'whatsapp',
    ad_id: null,
    original_media_type: 'voice',
    raw: data.raw_event,
    provider: 'wappi'
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

const data = $input.first().json;

return {
  channel: 'whatsapp',
  external_user_id: data.phone?.replace(/\+/g, ''),
  external_chat_id: data.chat_id,

  text: data.message_text,
  timestamp: data.timestamp,

  client_phone: data.phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'whatsapp',
    ad_id: null,
    original_media_type: data.has_photo ? 'photo' : data.has_video ? 'video' : 'text',
    raw: data.raw_event,
    provider: 'wappi'
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};
