parameters.jsCode:
// Подготовка данных для отправки через MCP Telegram
const input = $input.first().json;
const outMessage = input.out_message || input;

// external_chat_id в формате tg_XXXXXXXXXX
let chatId = outMessage.external_chat_id || '';
if (chatId.startsWith('tg_')) {
  chatId = chatId.substring(3);  // Убираем префикс tg_
}

return {
  chat_id: chatId,
  text: outMessage.message_text || outMessage.text,
  bot_token: outMessage.bot_token,
  parse_mode: 'Markdown',
  tenant_id: outMessage.tenant_id,
  appeal_id: outMessage.appeal_id,
  client_id: outMessage.client_id,
  action_source: outMessage.action_source || 'response'
};

const response = $input.first().json;
const preparedData = $('Prepare Message').first().json;

if (!response.success) {
  console.error('Telegram send failed:', response);
}

// Определяем тип touchpoint
const actionSource = preparedData.action_source || '';
const touchpointDirection = (actionSource === 'promo' || actionSource === 'greeting' || actionSource === 'broadcast') ? 'promo' : 'outbound';

return {
  ...preparedData,
  telegram_response: response,
  message_id: response.message_id?.toString() || Date.now().toString(),
  sent_at: new Date().toISOString(),
  touchpoint_direction: touchpointDirection
};

