parameters.jsCode:
const trigger = $('Execute Workflow Trigger').first().json;
const chat = $('Fetch Chat Data').first().json;

const chatId = chat.telegram_id || chat.whatsapp_id || chat.vk_id || chat.avito_id;

return {
  appeal_id: trigger.appeal_id,
  tenant_id: trigger.tenant_id,
  operator_id: trigger.operator_id,
  response_text: trigger.response_text,
  media_type: trigger.media_type,
  media_data: trigger.media_data,
  action_source: trigger.action_source,
  channel: trigger.channel || chat.channel || (chat.telegram_id ? 'telegram' : chat.whatsapp_id ? 'whatsapp' : chat.vk_id ? 'vk' : 'avito'),
  chat_id: chatId,
  external_chat_id: chatId,
  telegram_id: chat.telegram_id,
  phone: chat.phone,
  client_id: chat.client_id,
  // Определяем тип touchpoint
  // Если action_source === 'promo' или 'greeting' → promo
  // Иначе → outbound
  touchpoint_direction: (trigger.action_source === 'promo' || trigger.action_source === 'greeting' || trigger.action_source === 'broadcast') ? 'promo' : 'outbound'
};

parameters.jsonBody:
