const update = $input.first().json;

// Извлекаем сообщение
const msg = update.message || update.edited_message;
if (!msg) {
  return { skip: true, reason: 'no_message' };
}

// Проверяем что это не бот
if (msg.from?.is_bot) {
  return { skip: true, reason: 'bot_message' };
}

const chatId = msg.chat?.id?.toString();
const userId = msg.from?.id?.toString();
const messageId = msg.message_id?.toString();

// Текст сообщения
let messageText = msg.text || msg.caption || '';

// Проверяем медиа
const hasPhoto = !!msg.photo;
const hasVoice = !!msg.voice;
const hasAudio = !!msg.audio;
const hasVideo = !!msg.video;
const hasVideoNote = !!msg.video_note;
const hasDocument = !!msg.document;
const hasSticker = !!msg.sticker;

// Для голосовых нужна транскрибация
const needsTranscription = hasVoice || hasAudio || hasVideoNote;

// Данные отправителя
const senderName = [msg.from?.first_name, msg.from?.last_name].filter(Boolean).join(' ') || msg.from?.username || null;
const senderUsername = msg.from?.username || null;

// File ID для скачивания медиа
let fileId = null;
if (hasVoice) fileId = msg.voice.file_id;
else if (hasAudio) fileId = msg.audio.file_id;
else if (hasVideoNote) fileId = msg.video_note.file_id;
else if (hasPhoto) fileId = msg.photo[msg.photo.length - 1].file_id;
else if (hasVideo) fileId = msg.video.file_id;
else if (hasDocument) fileId = msg.document.file_id;

return {
  skip: false,
  needs_transcription: needsTranscription,
  file_id: fileId,
  raw_update: update,
  raw_message: msg,
  chat_id: chatId,
  user_id: userId,
  message_id: messageId,
  message_text: messageText,
  timestamp: msg.date ? new Date(msg.date * 1000).toISOString() : new Date().toISOString(),
  sender_name: senderName,
  sender_username: senderUsername,
  chat_type: msg.chat?.type,
  has_photo: hasPhoto,
  has_voice: hasVoice || hasAudio,
  has_video: hasVideo || hasVideoNote,
  has_document: hasDocument,
  has_sticker: hasSticker
};

const data = $('Extract Telegram Data').first().json;
const transcription = $input.first().json.text || '';

let messageText = data.message_text;
if (transcription) {
  messageText = (messageText ? messageText + '\n\n' : '') + transcription;
}

return {
  channel: 'telegram',
  external_user_id: data.user_id,
  external_chat_id: data.chat_id,

  text: messageText,
  timestamp: data.timestamp,

  client_phone: null,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: true,
    voice_transcribed_text: transcription,
    has_images: data.has_photo,
    images: [],
    has_video: data.has_video,
    videos: [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'telegram',
    ad_id: null,
    original_media_type: 'voice',
    raw: data.raw_message,
    chat_type: data.chat_type,
    sender_username: data.sender_username
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

const data = $input.first().json;

return {
  channel: 'telegram',
  external_user_id: data.user_id,
  external_chat_id: data.chat_id,

  text: data.message_text || '[Сообщение без текста]',
  timestamp: data.timestamp,

  client_phone: null,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: data.has_photo,
    images: [],
    has_video: data.has_video,
    videos: [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'telegram',
    ad_id: null,
    original_media_type: data.has_photo ? 'photo' : data.has_video ? 'video' : data.has_sticker ? 'sticker' : 'text',
    raw: data.raw_message,
    chat_type: data.chat_type,
    sender_username: data.sender_username
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

// Подготавливаем сообщение для Redis очереди
const data = $input.first().json;

return {
  message_json: JSON.stringify(data),
  batch_key: `${data.channel}:${data.external_chat_id}`
};
