const body = items[0].json || {};
const val = body.payload?.value || body;
const now = Date.now();
const rand = Math.random().toString(36).slice(2, 10);
const message_id = body.message_id || `msg_${now}_${rand}`;
const trace_id = body.trace_id || `trace_${now}_${rand}`;
return [{
  json: {
    channel: "avito",
    bot_token: body.client_id,
    external_user_id: val.author_id?.toString(),
    external_chat_id: val.chat_id,
    client_name: val.author?.name,
    text: val.content?.text || "",
    timestamp: val.created ? new Date(val.created * 1000).toISOString() : new Date().toISOString(),
    media: val.attachments || {},
    meta: {
      ad_id: val.item_id,
      chat_type: val.chat_type,
      ...body.meta
    },
    message_id,
    trace_id
  }
}];
