const body = items[0].json || {};
const now = Date.now();
const rand = Math.random().toString(36).slice(2, 10);
const message_id = body.message_id || `msg_${now}_${rand}`;
const trace_id = body.trace_id || `trace_${now}_${rand}`;
return [{
  json: {
    channel: "form",
    external_user_id: body.email || body.phone || body.external_user_id,
    external_chat_id: body.form_id || body.external_chat_id || `form_${now}`,
    client_name: body.name || body.client_name,
    client_phone: body.phone || body.client_phone,
    text: body.message || body.text || "",
    timestamp: body.timestamp || new Date().toISOString(),
    media: body.media || {},
    meta: body.meta || {},
    message_id,
    trace_id
  }
}];
