const body = items[0].json || {};
const msg = body.message || body;
const now = Date.now();
const rand = Math.random().toString(36).slice(2, 10);
const message_id = body.message_id || msg.id || `msg_${now}_${rand}`;
const trace_id = body.trace_id || `trace_${now}_${rand}`;
return [{
  json: {
    channel: "max",
    bot_token: body.bot_id,
    external_user_id: (msg.from && msg.from.id) || msg.from_id,
    external_chat_id: msg.chat_id,
    client_name: msg.from?.name,
    client_phone: msg.from?.phone,
    text: msg.text || "",
    timestamp: msg.timestamp ? new Date(msg.timestamp * 1000).toISOString() : new Date().toISOString(),
    media: msg.media || {},
    meta: body.meta || {},
    message_id,
    trace_id
  }
}];
