const body = items[0].json || {};
const now = Date.now();
const rand = Math.random().toString(36).slice(2, 10);
const message_id = body.message_id || `msg_${now}_${rand}`;
const trace_id = body.trace_id || `trace_${now}_${rand}`;
const obj = body.object?.message || body.message || body;
return [{
  json: {
    channel: "vk",
    bot_token: body.group_id,
    external_user_id: obj.from_id?.toString(),
    external_chat_id: obj.peer_id?.toString(),
    text: obj.text || "",
    timestamp: obj.date ? new Date(obj.date * 1000).toISOString() : new Date().toISOString(),
    media: obj.attachments || {},
    meta: body.meta || {},
    message_id,
    trace_id
  }
}];
