const body = items[0].json || {};
const now = Date.now();
const rand = Math.random().toString(36).slice(2, 10);
const message_id = body.message_id || `msg_${now}_${rand}`;
const trace_id = body.trace_id || `trace_${now}_${rand}`;
let phone = body.chatId || body.external_chat_id || body.sender || body.external_user_id;
if (phone && typeof phone === 'string') {
  phone = phone.replace(/@c\.us$/, '');
  if (!phone.startsWith('+')) phone = '+' + phone;
}
return [{
  json: {
    channel: "whatsapp",
    bot_token: body.profile_id,
    external_user_id: phone,
    external_chat_id: body.chatId || body.external_chat_id,
    client_name: body.pushname || body.senderName || body.client_name,
    client_phone: phone,
    text: body.text || "",
    timestamp: body.timestamp ? new Date(body.timestamp * 1000).toISOString() : new Date().toISOString(),
    media: body.media || {},
    meta: body.meta || {},
    message_id,
    trace_id
  }
}];
